class CajeroController:
    def __init__(self):
        self.cajero = CajeroAutomatico()
        self.view = CajeroView()
    def run(self):
        while True:
            self.view.show_menu()
            option = int(input("Ingrese una opción: "))
            if option == 1:
                self.cajero.depositarSaldo()

class CajeroAutomatico:
    def __init__(self):
        self.saldo = 0
    def depositarSaldo(self):
        print("Ingresar Saldo")
        monto = float(input("Ingrese monto a depositar: "))
        self.saldo += monto
        print(f"Se depositaron {monto} unidades. Saldo total: {self.saldo}")
    def girarSaldo(self):
        print("Girando saldo")

class CajeroView:
    @staticmethod
    def show_menu():
        print("\nOpciones del menú\n")
        print("1. Depositar Saldo")
        print("2. Girar saldo")
        print("3. Ver Saldo en la cuenta")
        print("4. Salir")
